mapper.select:
select entrp_nm as entrpNm ,load_addr as loadAddr ,city_do_cd as cityDoCd ,city_gn_gu_cd as cityGuguCd ,xpos_lo as xposIo ,ypos_la as yposIa ,area_nm as areaNm ,homepage_url as homepageUrl ,tel_no as telNo ,reprsnt_menu_nm as reprsntMenuNm ,menu_pc as menuPc ,base_ymd as baseYmd ,id as id ,image as image from breadstore
AND entrp_nm like CONCAT('%', #{keyword}, '%')
LIMIT #{startList}, #{listSize}

mapper.update:
update
	breadstore
set
	naver_place_url = #{naverPlaceUrl}
	, business_hours = #{businessHours}
where id=#{id}
