mapper.select:
select a.entrp_nm as entrpNm ,a.load_addr as loadAddr ,a.business_hours as businessHours ,a.city_do_cd as cityDoCd ,a.city_gn_gu_cd as cityGuguCd ,a.xpos_lo as xposIo ,a.ypos_la as yposIa ,a.area_nm as areaNm ,a.homepage_url as homepageUrl ,a.tel_no as telNo ,a.reprsnt_menu_nm as reprsntMenuNm ,a.menu_pc as menuPc ,a.base_ymd as baseYmd ,a.id as id ,a.image as image ,(select count(*) from breadstorereview b where b.bread_store_key = a.id and b.delete_yn='N') as reviewCnt from breadstore a where a.xpos_lo like CONCAT(#{xposIo}, '%') and a.ypos_la like CONCAT(#{yposIa}, '%')
AND a.entrp_nm like CONCAT('%', #{keyword}, '%')
LIMIT #{startList}, #{listSize}

mapper.update:
update
	breadstore
set
	naver_place_url = #{naverPlaceUrl}
	, business_hours = #{businessHours}
where id=#{id}
